(617, 'woo_framework_template', 'a:22:{i:0;a:3:{s:4:"name";s:14:"Admin Settings";s:4:"icon";s:7:"general";s:4:"type";s:7:"heading";}i:1;a:6:{s:4:"name";s:21:"Super User (username)";s:4:"desc";s:260:"Enter your <strong>username</strong> to hide the Framework Settings and Update Framework from other users. Can be reset from the <a href="http://theabsorbingbrand.com/upbeat/wp-admin/options.php">WP options page</a> under <code>framework_woo_super_user</code>.";s:2:"id";s:24:"framework_woo_super_user";s:3:"std";s:0:"";s:5:"class";s:4:"text";s:4:"type";s:4:"text";}i:2;a:5:{s:4:"name";s:33:"Disable Backup Settings Menu Item";s:4:"desc";s:73:"Disable the <strong>Backup Settings</strong> menu item in the theme menu.";s:2:"id";s:32:"framework_woo_backupmenu_disable";s:3:"std";s:0:"";s:4:"type";s:8:"checkbox";}i:3;a:5:{s:4:"name";s:25:"Theme Update Notification";s:4:"desc";s:101:"This will enable notices on your theme options page that there is an update available for your theme.";s:2:"id";s:35:"framework_woo_theme_version_checker";s:3:"std";s:0:"";s:4:"type";s:8:"checkbox";}i:4;a:5:{s:4:"name";s:32:"WooFramework Update Notification";s:4:"desc";s:107:"This will enable notices on your theme options page that there is an update available for the WooFramework.";s:2:"id";s:39:"framework_woo_framework_version_checker";s:3:"std";s:0:"";s:4:"type";s:8:"checkbox";}i:5;a:3:{s:4:"name";s:14:"Theme Settings";s:4:"icon";s:7:"general";s:4:"type";s:7:"heading";}i:6;a:5:{s:4:"name";s:26:"Remove Generator Meta Tags";s:4:"desc";s:81:"This disables the output of generator meta tags in the HEAD section of your site.";s:2:"id";s:31:"framework_woo_disable_generator";s:3:"std";s:0:"";s:4:"type";s:8:"checkbox";}i:7;a:5:{s:4:"name";s:17:"Image Placeholder";s:4:"desc";s:151:"Set a default image placeholder for your thumbnails. Use this if you want a default image to be shown if you haven''t added a custom image to your post.";s:2:"id";s:27:"framework_woo_default_image";s:3:"std";s:0:"";s:4:"type";s:6:"upload";}i:8;a:5:{s:4:"name";s:29:"Disable Shortcodes Stylesheet";s:4:"desc";s:76:"This disables the output of shortcodes.css in the HEAD section of your site.";s:2:"id";s:32:"framework_woo_disable_shortcodes";s:3:"std";s:0:"";s:4:"type";s:8:"checkbox";}i:9;a:5:{s:4:"name";s:39:"Output "Tracking Code" Option in Header";s:4:"desc";s:112:"This will output the <strong>Tracking Code</strong> option in your header instead of the footer of your website.";s:2:"id";s:32:"framework_woo_move_tracking_code";s:3:"std";s:5:"false";s:4:"type";s:8:"checkbox";}i:10;a:3:{s:4:"name";s:8:"Branding";s:4:"icon";s:4:"misc";s:4:"type";s:7:"heading";}i:11;a:5:{s:4:"name";s:20:"Options panel header";s:4:"desc";s:50:"Change the header image for the WooThemes Backend.";s:2:"id";s:34:"framework_woo_backend_header_image";s:3:"std";s:0:"";s:4:"type";s:6:"upload";}i:12;a:5:{s:4:"name";s:18:"Options panel icon";s:4:"desc";s:56:"Change the icon image for the WordPress backend sidebar.";s:2:"id";s:26:"framework_woo_backend_icon";s:3:"std";s:0:"";s:4:"type";s:6:"upload";}i:13;a:5:{s:4:"name";s:20:"WordPress login logo";s:4:"desc";s:92:"Change the logo image for the WordPress login page.<br /><br />Optimal logo size is 274x63px";s:2:"id";s:31:"framework_woo_custom_login_logo";s:3:"std";s:0:"";s:4:"type";s:6:"upload";}i:14;a:6:{s:4:"name";s:19:"WordPress login URL";s:4:"desc";s:72:"Change the URL that the logo image on the WordPress login page links to.";s:2:"id";s:35:"framework_woo_custom_login_logo_url";s:3:"std";s:0:"";s:5:"class";s:4:"text";s:4:"type";s:4:"text";}i:15;a:6:{s:4:"name";s:26:"WordPress login logo Title";s:4:"desc";s:63:"Change the title of the logo image on the WordPress login page.";s:2:"id";s:37:"framework_woo_custom_login_logo_title";s:3:"std";s:0:"";s:5:"class";s:4:"text";s:4:"type";s:4:"text";}i:16;a:3:{s:4:"name";s:17:"WordPress Toolbar";s:4:"icon";s:6:"header";s:4:"type";s:7:"heading";}i:17;a:5:{s:4:"name";s:25:"Disable WordPress Toolbar";s:4:"desc";s:30:"Disable the WordPress Toolbar.";s:2:"id";s:31:"framework_woo_admin_bar_disable";s:3:"std";s:0:"";s:4:"type";s:8:"checkbox";}i:18;a:5:{s:4:"name";s:44:"Enable the WooFramework Toolbar enhancements";s:4:"desc";s:128:"Enable several WooFramework-specific enhancements to the WordPress Toolbar, such as custom navigation items for "Theme Options".";s:2:"id";s:36:"framework_woo_admin_bar_enhancements";s:3:"std";s:0:"";s:4:"type";s:8:"checkbox";}i:19;a:3:{s:4:"name";s:11:"PressTrends";s:4:"icon";s:11:"presstrends";s:4:"type";s:7:"heading";}i:20;a:5:{s:4:"name";s:27:"Enable PressTrends Tracking";s:4:"desc";s:44:"Enable sending of usage data to PressTrends.";s:2:"id";s:32:"framework_woo_presstrends_enable";s:3:"std";s:5:"false";s:4:"type";s:8:"checkbox";}i:21;a:5:{s:4:"name";s:20:"What is PressTrends?";s:4:"desc";s:0:"";s:2:"id";s:30:"framework_woo_presstrends_info";s:3:"std";s:353:"PressTrends is a simple usage tracker that allows us to see how our customers are using WooThemes themes - so that we can help improve them for you. <strong>None</strong> of your personal data is sent to PressTrends.<br /><br />For more information, please view the PressTrends <a href="http://presstrends.io/privacy" target="_blank">privacy policy</a>.";s:4:"type";s:4:"info";}}', 'yes'),
